	$TotalCachedFiles = count($CacheDirOldFilesAge);
	$DeletedKeys[\'maxfiles\'] = array();
	foreach ($CacheDirOldFilesAge as $fullfilename => $filedate) {
		if ($TotalCachedFiles > $cache_maxfiles) {
			if (@unlink($fullfilename)) {
				--$TotalCachedFiles;
